    <?php                        // function to insert data in the database         function deleteSavedTripFromDB($db) 		{                        // get the data from the front end            $userID = $_POST['userID'];			$title = $_POST['title'];                        // check the values            if( 			!$userID ||			!$title			) 			{                echo 'One or more fields are empty.';                return;            }            else 			{                // escape special characters in a string for use in the SQL statement 			}         			$sqlQuery = "DELETE FROM savedtrips WHERE userID= :userID && title = :title";					//prepare the query            $query = $db->prepare($sqlQuery);                        //execute the query            $query->execute(array(':userID'=>$userID, ':title'=>$title));            // check if the customer was successfully inserted in the database            if ($query) {				//once the SQL write operation is complete, redirect to a receipt page                header("Location: savedTrips.php");				die();            }            else {                //redirect to an error page                header("Location: error.php");				die();            }                    }                        /* Main body */        //connect to the DB        $dsn = 'mysql:host=127.0.0.1;dbname=dream-travel-v05';        $user = 'root';        $password = '';                try {            $db = new PDO($dsn, $user, $password);            $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        } catch (PDOException $e) {            //           echo 'Connection failed: ' . $e->getMessage();            die('Sorry, database problem');        }        		deleteSavedTripFromDB($db);                ?>