    <?php                        // function to insert data in the database         function insertSavedTripInDB($db) {                        // get the data from the front end            $tripID = $_POST['tripID'];            $userID = $_POST['userID'];			$title = $_POST['title'];				            // check the values            if( 			!$tripID ||			!$userID ||			!$title			) 				{					echo 'One or more fields are empty.';					return;				}            else {                // escape special characters in a string for use in the SQL statement            }						$sqlQuery = "INSERT INTO savedtrips (ID, userID, tripID, title) VALUES (?, ?, ?, ?)";		
	    //prepare the query            $query = $db->prepare($sqlQuery);                        //execute the query            $query->execute(array(NULL, $userID, $tripID, $title));
 			//check if the booking was successfully inserted in the database            if ($query) {				//once the SQL write operation is complete, redirect to a receipt page                header("Location: savedTrips.php");				die();            }            else {                //redirect to an error page                header("Location: error.php");				die();            }        }		                /* Main body */        //connect to the DB        $dsn = 'mysql:host=127.0.0.1;dbname=dream-travel-v05';        $user = 'root';        $password = '';                try {            $db = new PDO($dsn, $user, $password);            $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        } catch (PDOException $e) {            //           echo 'Connection failed: ' . $e->getMessage();            die('Sorry, database problem');        }                insertSavedTripInDB($db);        ?>