    <?php                        // function to insert data in the database         function insertTripInDB($db) {                        // get the data from the front end            $price = $_POST['price'];            $title = $_POST['title'];            $startLocation = $_POST['startLocation'];            $finishLocation = $_POST['finishLocation'];            $country = $_POST['country'];            $departingDate = $_POST['departingDate'];            $finishingDate = $_POST['finishingDate'];            $placesLeft = $_POST['placesLeft'];				$map = $_POST['map'];            $headerImage = $_POST['headerImage'];            $thumbnail = $_POST['thumbnail'];	            // check the values            if( 			!$price ||			!$title ||			!$startLocation ||			!$finishLocation ||			!$country ||			!$departingDate || 			!$finishingDate || 			!$placesLeft ||			!$map || 			!$headerImage || 			!$thumbnail			) 				{					echo 'One or more fields are empty.';					return;				}            else {                // escape special characters in a string for use in the SQL statement            }						$date1 = new DateTime("$departingDate");		$date2 = new DateTime("$finishingDate");		$interval = $date1->diff($date2);		$noOfDays = $interval->days + 1;					$sqlQuery = "INSERT INTO Trips (price, title, startLocation, finishLocation, country, departingDate, finishingDate, noOfDays, placesLeft, map, headerImage, thumbnail) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";		
	    //prepare the query            $query = $db->prepare($sqlQuery);                        //execute the query            $query->execute(array($price, $title, $startLocation, $finishLocation, $country, $departingDate, $finishingDate, $noOfDays, $placesLeft, $map, $headerImage, $thumbnail));
             // check if the customer was successfully inserted in the database            if ($query) {				//once the SQL write operation is complete, redirect to a receipt page                header("Location: ../index.php");				die();            }            else {                //redirect to an error page                header("Location: error.php");				die();            }                    }                        /* Main body */        //connect to the DB        $dsn = 'mysql:host=127.0.0.1;dbname=dream-travel-v05';        $user = 'root';        $password = '';                try {            $db = new PDO($dsn, $user, $password);            $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        } catch (PDOException $e) {            //           echo 'Connection failed: ' . $e->getMessage();            die('Sorry, database problem');        }                insertTripInDB($db);                ?>