    <?php                        // function to insert data in the database         function insertUserInDB($db) {                        // get the data from the front end            $name = $_POST['flname'];            $email = $_POST['email'];            $username = $_POST['username'];            $password = $_POST['password'];            // check the values            if( !$name || !$email || !$username || !$password) {                echo 'One or more fields are empty.';                return;            }            else {                // escape special characters in a string for use in the SQL statement                // ENCRYPT THE PASSWORD                $encrypt_pass = password_hash($password, PASSWORD_DEFAULT);           }                        // create a query           $sqlQuery = "INSERT INTO Users (name, email, username, encrypt_pass) VALUES (:name, :email, :username, :password)";                        //prepare the query            $query = $db->prepare($sqlQuery);                        //execute the query            $query->execute(array(                                  ':name' => $name,                                  ':email' => $email,                                  ':username' => $username,                                  ':password' => $encrypt_pass                                  ));                                    /*            $sqlQuery = "INSERT INTO Students VALUES (?, ?, ?)";            //prepare the query            $query = $db->prepare($sqlQuery);                        //execute the query            $query->execute(array($sid, $name, $email));*/                        // check if the user was successfully inserted in the database            if ($query) {                echo 'The user was inserted in the database';            }            else {                // print the error generated                echo "The user was not inserted in the database:";            }                    }                //function to associate user ID to group ID        function insertGroupUserInDB($db) {                        // get the data from the front end            $groupID = 1;							// Look up the most recent ID in users			$query2 = "SELECT MAX(ID) AS 'ID'			FROM users";						//send the query to the db			$result = $db->query($query2);		    $found = true;            while ($row = $result->fetch(PDO::FETCH_ASSOC)) 			{             				  $found = false;				  //get the ID found in the database				  $userID = $row['ID'];				  $found = true;				  break;            }						//INSERT INTO `bookings_users` (`ID`, `booking_id`, `user_id`) VALUES (NULL, '6', '3');			$sqlQuery2 = "INSERT INTO `groups_users` (`ID`, `group_id`, `user_id`) VALUES (?, ?, ?)";					//prepare the query            $query3 = $db->prepare($sqlQuery2);                        //execute the query			$query3->execute(array(NULL, $groupID, $userID));        }												        /* Main body */        //connect to the DB        $dsn = 'mysql:host=127.0.0.1;dbname=dream-travel-v05';        $user = 'root';        $dbpassword = '';                try {            $db = new PDO($dsn, $user, $dbpassword);            $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        } catch (PDOException $e) {            //           echo 'Connection failed: ' . $e->getMessage();            die('Sorry, database problem');        }                insertUserInDB($db);		insertGroupUserInDB($db);                ?>