return $tripID;		}function displaySave($db, $tripID)	{				$userID = $_SESSION['user_id'];$title = $_GET['title'];		//create query 1$sth = $db->prepare("SELECT * FROM savedtrips WHERE userID= :userID && title = :title");$sth->execute(array(':userID'=>$userID, ':title'=>$title));$userID = $_SESSION['user_id'];	$title = $_GET['title'];	//change the tripID value for each new Trip page 			if(!$sth->rowCount() > 0) 		{			print ("				<form action=\"insertSavedTripInDB.php\" method=\"post\">				<input type=\"hidden\" name=\"tripID\" value=\"$tripID\" />				<input type=\"hidden\" name=\"userID\" value=\"$userID\" />				<input type=\"hidden\" name=\"title\" value=\"$title\" />				<input type=\"submit\" name=\"Submit\" value = \"Save\" />				</form>			");		}	else 		{			print ("				<form action=\"deleteSavedTripFromDB.php\" method=\"post\">				<input type=\"hidden\" name=\"userID\" value=\"$userID\" />				<input type=\"hidden\" name=\"title\" value=\"$title\" />				<input type=\"submit\" name=\"Submit\" value = \"Unsave\" />				</form>			");		}}function displayMap($db)	{				$userID = $_SESSION['user_id'];$title = $_GET['title'];// create a query 100$query100 = "SELECT * FROM Trips 	WHERE placesLeft > 0 && departingDate >= CURRENT_DATE() && title = :title GROUP BY(title) ORDER BY tripID DESC";$params100 = array(			'title' => $title			);$result100 = $db->prepare($query100);$result100->execute($params100);$row = $result100->fetch(PDO::FETCH_ASSOC);		print("<iframe width='100%' height='350px' frameborder='0' style='border:0' src='$row[map]' allowfullscreen></iframe>");}function displayDates($db)	{				$userID = $_SESSION['user_id'];$title = $_GET['title'];// create a query 2$query = "SELECT * FROM Trips	WHERE placesLeft > 0 && departingDate >= CURRENT_DATE() && title = :title";$params = array(			'title' => $title			);$result = $db->prepare($query);$result->execute($params);$userID = $_SESSION['user_id'];	