    <?php                        // function to insert data in the database         function updateUserInDB($db) {                        // get the data from the front end            $id = $_POST['id'];            $name = $_POST['flname'];            $email = $_POST['email'];                        // check the values            if( !$id || !$name || !$email) {                echo 'One or more fields are empty.';                return;            }            else {                // escape special characters in a string for use in the SQL statement            }		   		$sqlQuery = "UPDATE Users SET Name=?, Email=? WHERE ID=?";		
	    //prepare the query            $query = $db->prepare($sqlQuery);                        //execute the query            $query->execute(array($name, $email, $id));
             // check if the customer was successfully inserted in the database            if ($query) {				//once the SQL write operation is complete, redirect to a receipt page                header("Location: receipt.php");				die();            }            else {                //redirect to an error page                header("Location: error.php");				die();            }                    }                        /* Main body */        //connect to the DB        $dsn = 'mysql:host=127.0.0.1;dbname=dream-travel-v05';        $user = 'root';        $password = '';                try {            $db = new PDO($dsn, $user, $password);            $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        } catch (PDOException $e) {            //           echo 'Connection failed: ' . $e->getMessage();            die('Sorry, database problem');        }                updateUserInDB($db);                ?>